const {scrape} = require('./index');

describe('scrape (E2E Test)', () => {
  it('captivoo', async() => {
    const page = 'captivoo';
    const result = await scrape(page);

    expect(result.creationDate).to.exist;
  }).timeout(10000);

  it('Wingnuts-at-Georgia-Tech-214578745247617', async() => {
    const page = 'Wingnuts-at-Georgia-Tech-214578745247617';
    const result = await scrape(page);

    expect(result.url).to.exist;
    expect(result.normalised).to.exist;

    expect(result.phone).to.exist;
    expect(result.streetAddress).to.exist;
    expect(result.addressLocality).to.exist;
    expect(result.postalCode).to.exist;
    expect(result.email).to.exist;
    expect(result.website).to.exist;
    expect(result.ratingAggregate).to.exist;
    expect(result.ratingCount).to.exist;
    expect(result.about).to.exist;
  }).timeout(10000);
});